package org.vaddin.demo.view;

import com.vaadin.flow.component.applayout.AppLayout;
import com.vaadin.flow.component.applayout.DrawerToggle;
import com.vaadin.flow.component.html.H1;
import com.vaadin.flow.component.html.Image;
import com.vaadin.flow.component.html.Span;
import com.vaadin.flow.component.orderedlayout.VerticalLayout;
import com.vaadin.flow.component.orderedlayout.FlexComponent;
import com.vaadin.flow.component.sidenav.SideNav;
import com.vaadin.flow.component.orderedlayout.Scroller;
import com.vaadin.flow.component.sidenav.SideNavItem;
import com.vaadin.flow.theme.lumo.LumoUtility;

public class NavBarView extends AppLayout {

    public NavBarView() {
        DrawerToggle toggle = new DrawerToggle();
        H1 title = new H1("Dashboard");
        title.getStyle()
                .set("font-size", "var(--lumo-font-size-l)")
                .set("margin", "0");

        addToNavbar(toggle, title);
        setPrimarySection(Section.DRAWER);

        Image logo = new Image("data:image/jpeg;base64,",
                "App Logo");
        logo.setWidth("250px");
        logo.setHeight("80px");
        Span heading = new Span("My App");
        heading.addClassNames(LumoUtility.FontWeight.BOLD, LumoUtility.FontSize.XLARGE);

        VerticalLayout drawerHeader = new VerticalLayout(logo, heading);
        drawerHeader.setWidthFull();
        drawerHeader.setAlignItems(FlexComponent.Alignment.CENTER);
        drawerHeader.addClassName(LumoUtility.Padding.SMALL);

        SideNav nav = getSideNav();
        Scroller scroller = new Scroller(nav);
        scroller.addClassName(LumoUtility.Padding.SMALL);

        VerticalLayout drawerLayout = new VerticalLayout(drawerHeader, scroller);
        drawerLayout.setPadding(false);
        drawerLayout.setSpacing(false);
        drawerLayout.setSizeFull();

        addToDrawer(drawerLayout);
    }

    private SideNav getSideNav() {
        SideNav nav = new SideNav();
        nav.addItem(new SideNavItem("Register User", "registration"));
        nav.addItem(new SideNavItem("User List", "user-list"));
        return nav;
    }
}
